import React, { useState } from 'react'
import ExpenseItem from './ExpenseItem';
import "./Expenses.css";
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';

function Expenses(props) {

  const [filter, setFilter] = useState('2020');

  let fitleredInfoText = "2019, 2021 & 2022";

  if (filter === "2019") {
    fitleredInfoText = "2020, 2021 & 2022";
  } else if (filter === "2021") {
    fitleredInfoText = ""
  }

  const filterExpensesHandler = (selectedYear) => {
    setFilter(selectedYear);
    console.log('selectedYear is:');
    console.log(selectedYear);
  };

  return (
    <div>
      <Card className='expenses'>
          <ExpensesFilter selected={filter} onFilterExpenses={filterExpensesHandler}/>
          <p>Data for years 2019, 2020 & 2022 is hidden.</p>
          <ExpenseItem title={props.expenses[0].title} amount={props.expenses[0].amount} date={props.expenses[0].date}/>
          <ExpenseItem title={props.expenses[1].title} amount={props.expenses[1].amount} date={props.expenses[1].date}/>
          <ExpenseItem title={props.expenses[2].title} amount={props.expenses[2].amount} date={props.expenses[2].date}/>
          <ExpenseItem title={props.expenses[3].title} amount={props.expenses[3].amount} date={props.expenses[3].date}/>
      </Card>
    </div>
  )
}

export default Expenses;
